import './card.css'

function Card({title, index, titleButton, textList, nameImage, direction}){
    return(

        <div style={{
            display: "flex",
            flexDirection: direction,
            alignItems: "center",
            marginTop: "10px"
        }}>
            <div className='card'>

                <div className='header-card' style={{background: `url(require("../assets/${nameImage})`}}>
                    <div style={{
                       display: "flex",
                       flexDirection: dir,
                       justifyContent: "center",
                       alignItems: "center",
                       color: "white",
                       width: "112px",
                       height: "44.43px",
                       backgroundColor: "#E14BDB",
                    }}>{index}</div>
                    <h2>{title}</h2>
                </div>

                <div className='container-text'>
                    {textList.map((text, index) => <p key={index}>{text}</p>)}
                </div>

                <button>{titleButton}</button>

            </div>
        </div>
    );
}

export default Card;
