import './main.css'
import Card from '../Card/card';

function Main(){
    return(
        <div className='container'>

            <Card title={"O que estamos fazendo para ajudar o seu negócio"} index={"1"}  titleButton={"VER MAIS"} textList={["Durante todo o período de quarentena, estamos à total disposição de cada um de vocês, clientes.O COVID-19 chegou desestabilizando o país e trazendo consigo uma grande incógnita quanto a proporção disso no país, nas pessoas e principalmente, na economia.", "Nosso compromisso com você durante essa fase, é para assegurar que o seu negócio seja o  menos afetado possível, mantendo sua base de clientes fidelizados sólida e segura!"]} nameImage={"img1.png"}/>

            <Card title={"Ferramentas indispensáveis nesse momento"} index={"2"}  titleButton={"CONHEÇA AGORA O QUE ESTÁ A SUA DISPOSIÇÃO"} textList={["Você sabe que magos contam com bons artifícios para fazer sua magia acontecer. Com nós FideWizards não é diferente!", "Contamos com ferramentas poderosas para fazer nossa mágica acontecer e fidelizar seus clientes. Especialmente neste período em que o COVID-19 nos pegou desprevenidos, vamos compartilhar com você nossas ferramentas e fórmulas mágicas, afinal, queremos ver o seu negócio se destacando e superando essa fase, como um toque de mágica."]} nameImage={"img2.png"}/>

            <div className='linha-vertical'></div>

            <div className='bo'></div>

            <Card title={"Como tornar a magia real"} index={"3"}  titleButton={"VER MATERIAIS"} textList={["Durante todo o período de quarentena, estamos à total disposição de cada um de vocês, clientes.O COVID-19 chegou desestabilizando o país e trazendo consigo uma grande incógnita quanto a proporção disso no país, nas pessoas e principalmente, na economia.", "Nosso compromisso com você durante essa fase, é para assegurar que o seu negócio seja o  menos afetado possível, mantendo sua base de clientes fidelizados sólida e segura!"]} nameImage={"img3.png"}/>

            <Card title={"Conheça alguns clientes que compartilharam suas experiências"} index={"4"}  titleButton={"VER MAIS"} textList={["Estamos a todo vapor neste período de quarentena, auxiliando e configurando as ferramentas para que nossos clientes possam manter uma comunicação perene com seus respectivos públicos.Neste momento, nosso objetivo principal é manter o seu negócio saudável e com mínimo de danos ocasionados pela crise!", "Manter o contato com a sua base de clientes é a melhor estratégia, seja oferecendo seus produtos com entregas delivery fazendo vendas antecipadas, provendo ações para a retomada pós quarentena, e outras diversas opções estratégicas.", "O importante é manter-se presente no dia a dia dos clientes, para que eles lembrem-se da sua marca e sejam motivados a voltar para o seu estabelecimento assim que tudo se acalmar, e mais, optar pelos seus produtos ou serviços se isso for uma opção neste período.", "Confira alguns feedbacks de clientes que estão em busca de soluções para manter seus estabelecimentos ativos e saudáveis e contaram com a gente para isso!"]} nameImage={"img4.png"}/>

            <img className='potion' src={require('../assets/formula.png')}/>

            <p className='phrase'>Fique ligado, mais dicas estão saindo do laboratório!</p>

        </div>
    );
}

export default Main;
